// Process order:
sub spooler_process()
{
    my $order = $spooler_task->order;
    my $variable_set = $order->payload;
    $spooler_log->info( 'param_one=' . $variable_set->value( 'param_one' ) );
    $spooler_log->info( 'param_two=' . $variable_set->value( 'param_two' ) );
    return 1;
}
